import React, { Component } from 'react';
import './App.css';
import FromPage from './FromPage';
import RenderPage from './RenderPage';

class App extends Component {
  state={
    dataArr: [
      {movieName: '',
      duration: '',
      ratings: '',}
    ],

  }
  handleChange = ( e ) => {
    this.setState({
      dataArr: [{...this.state.dataArr,[e.target.name]: e.target.value }],
    })
  } 
  handleSubmit = ( e ) => {
    e.preventDefault();
    const dataArr = this.state.dataArr;
    console.log('form ', dataArr);
  }
  render () {
    return (
      <div className='main ui container'>
        <div className='ui row'>
          <FromPage 
            handleChange= { this.handleChange }
            handleSubmit= { this.handleSubmit }
          />
        </div>
        <div className='ui row'>
          <RenderPage 
            data = { this.state.dataArr }
          />
        </div>

      </div>
    );
  }
}

export default App;
